function simpleAJAX() {
  this.http = new XMLHttpRequest();
}

simpleAJAX.prototype.get = function (t, s) {
  this.http.open("GET", t, !0), this.http.onload = () => {
    200 === this.http.status ? s(null, this.http.responseText) : s("Error: " + this.http.status);
  }, this.http.send();
}, simpleAJAX.prototype.post = function (t, s, p) {
  this.http.open("POST", t, !0), this.http.setRequestHeader("Content-type", "application/json"), this.http.onload = () => {
    p(null, this.http.responseText);
  }, this.http.send(JSON.stringify(s));
}, simpleAJAX.prototype.put = function (t, s, p) {
  this.http.open("PUT", t, !0), this.http.setRequestHeader("Content-type", "application/json"), this.http.onload = () => {
    p(null, this.http.responseText);
  }, this.http.send(JSON.stringify(s));
}, simpleAJAX.prototype.delete = function (t, s) {
  this.http.open("DELETE", t, !0), this.http.onload = () => {
    200 === this.http.status ? s(null, "Post Deleted!") : s("Error: " + this.http.status);
  }, this.http.send();
};